import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import { Link } from "react-router-dom";
import "./TaskList.css"; // Assuming you have some styles for the task list
const TaskList = () => {
  const { token } = useContext(AuthContext);
  const [tasks, setTasks] = useState([]);
  const [error, setError] = useState(null);

  const fetchTasks = async () => {
    try {
      const res = await axios.get("http://localhost:8000/tasks/", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(res.data);
    } catch (err) {
      setError("Failed to fetch tasks");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this task?")) return;
    try {
      await axios.delete(`http://localhost:8000/tasks/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(tasks.filter((t) => t.id !== id));
    } catch {
      alert("Failed to delete task");
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div>
      <h2>Your Tasks</h2>
      <Link to="/tasks/new">+ Create New Task</Link>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {tasks.length === 0 && <p>No tasks found.</p>}
      <ul>
        {tasks.map((task) => (
          <li key={task.id} style={{ marginBottom: "15px" }}>
            <strong>{task.title}</strong> [{task.priority}]<br />
            Category: {task.category} | Status:{" "}
            {task.status ? "Completed" : "Pending"}<br />
            Due: {task.due_date || "N/A"}<br />
            <Link to={`/tasks/edit/${task.id}`} style={{ marginRight: "10px" }}>
              Edit
            </Link>
            <button onClick={() => handleDelete(task.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskList;
