import React, { useState } from "react";

export default function TaskForm({ onAdd }) {
  const [tasks, setTasks] = useState([
    { title: "", description: "", category: "", status: false },
  ]);

  function handleChange(index, key, value) {
    const updated = [...tasks];
    updated[index][key] = value;
    setTasks(updated);
  }

  function handleSubmit(e) {
    e.preventDefault();
    const valid = tasks.filter(t => t.title.trim());
    if (valid.length === 0) return;
    onAdd(valid);
    setTasks([{ title: "", description: "", category: "", status: false }]);
  }

  return React.createElement(
    "form",
    { onSubmit: handleSubmit },
    ...tasks.map((task, index) =>
      React.createElement(
        "div",
        { key: index, style: { marginBottom: 10 } },
        React.createElement("input", {
          type: "text",
          placeholder: "Title",
          value: task.title,
          onChange: e => handleChange(index, "title", e.target.value),
        }),
        React.createElement("br"),
        React.createElement("input", {
          type: "text",
          placeholder: "Description",
          value: task.description,
          onChange: e => handleChange(index, "description", e.target.value),
        }),
        React.createElement("br"),
        React.createElement("input", {
          type: "text",
          placeholder: "Category",
          value: task.category,
          onChange: e => handleChange(index, "category", e.target.value),
        }),
        React.createElement("br"),
        React.createElement("label", null,
          "Completed ",
          React.createElement("input", {
            type: "checkbox",
            checked: task.status,
            onChange: e => handleChange(index, "status", e.target.checked),
          })
        )
      )
    ),
    React.createElement(
      "button",
      { type: "button", onClick: () => setTasks([...tasks, { title: "", description: "", category: "", status: false }]) },
      "Add Another Task"
    ),
    React.createElement("br"),
    React.createElement("button", { type: "submit" }, "Create Tasks")
  );
}
