import React from "react";

export default function TaskList({ tasks, onUpdate, onDelete }) {
  return React.createElement(
    "ul",
    { style: { listStyle: "none", padding: 0 } },
    ...tasks.map(task =>
      React.createElement(
        "li",
        {
          key: task.id,
          style: {
            border: "1px solid #ccc",
            padding: 10,
            marginBottom: 10,
            borderRadius: 5,
          }
        },
        React.createElement("h3", null, task.title),
        React.createElement("p", null, task.description),
        React.createElement("p", null, "Category: ", task.category || "N/A"),
        React.createElement("p", null, "Status: ", task.status ? "Completed" : "Pending"),
        React.createElement("button", {
          onClick: () => onUpdate(task.id, { status: !task.status })
        }, task.status ? "Mark Pending" : "Mark Completed"),
        " ",
        React.createElement("button", {
          onClick: () => onDelete(task.id),
          style: { color: "red", marginLeft: 10 }
        }, "Delete")
      )
    )
  );
}
