import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';

function App() {
  const BACKEND_URL = 'http://127.0.0.1:8000';

  // State variables
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [category, setCategory] = useState('personal');
  const [statusFilter, setStatusFilter] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('');
  const [summary, setSummary] = useState({
    total_tasks: 0,
    completed_tasks: 0,
    pending_tasks: 0,
    completion_rate_percent: 0,
  });

  // Fetch tasks with optional filters
  function fetchTasks() {
    let url = new URL(BACKEND_URL + '/filter-tasks/');
    if (categoryFilter) url.searchParams.append('category', categoryFilter);
    if (statusFilter !== '') url.searchParams.append('status', statusFilter === 'true');

    fetch(url.toString())
      .then(res => {
        if (!res.ok) {
          if (res.status === 404) return [];
          throw new Error('Error fetching tasks');
        }
        return res.json();
      })
      .then(data => setTasks(data))
      .catch(() => setTasks([]));
  }

  // Fetch summary info
  function fetchSummary() {
    fetch(BACKEND_URL + '/tasks-summary/')
      .then(res => res.json())
      .then(data => setSummary(data))
      .catch(() =>
        setSummary({
          total_tasks: 0,
          completed_tasks: 0,
          pending_tasks: 0,
          completion_rate_percent: 0,
        })
      );
  }

  // Load tasks and summary initially and when filters change
  useEffect(() => {
    fetchTasks();
    fetchSummary();
  }, [categoryFilter, statusFilter]);

  // Add new task
  function handleAddTask() {
    if (title.trim() === '') return;

    const newTask = {
      title: title,
      description: '',
      category: category,
      status: false,
    };

    fetch(BACKEND_URL + '/tasks/', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify([newTask]) // backend expects list of tasks
    })
      .then(res => {
        if (!res.ok) throw new Error('Failed to create task');
        return res.json();
      })
      .then(() => {
        setTitle('');
        fetchTasks();
        fetchSummary();
      })
      .catch(err => {
        alert('Error adding task: ' + err.message);
      });
  }

  // Toggle task completion status
  function toggleComplete(task) {
    fetch(`${BACKEND_URL}/tasks/${task.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...task, status: !task.status }),
    })
      .then(res => res.json())
      .then(() => {
        fetchTasks();
        fetchSummary();
      });
  }

  // Delete a task
  function deleteTask(taskId) {
    fetch(`${BACKEND_URL}/tasks/${taskId}`, { method: 'DELETE' })
      .then(() => {
        fetchTasks();
        fetchSummary();
      });
  }

  return React.createElement(
    'div',
    { style: { maxWidth: 600, margin: 'auto', fontFamily: 'Arial, sans-serif', padding: 20 } },

    React.createElement('h2', null, 'Smart Task Tracker'),

    // Summary
    React.createElement(
      'div',
      { style: { marginBottom: 20, padding: 10, backgroundColor: '#f0f0f0', borderRadius: 6 } },
      React.createElement('div', null, `Total Tasks: ${summary.total_tasks}`),
      React.createElement('div', null, `Completed: ${summary.completed_tasks}`),
      React.createElement('div', null, `Pending: ${summary.pending_tasks}`),
      React.createElement('div', null, `Completion Rate: ${summary.completion_rate_percent}%`)
    ),

    // Filters
    React.createElement(
      'div',
      { style: { marginBottom: 20 } },
      React.createElement('label', null, 'Filter Category: '),
      React.createElement(
        'select',
        {
          value: categoryFilter,
          onChange: e => setCategoryFilter(e.target.value),
          style: { marginRight: 20 },
        },
        React.createElement('option', { value: '' }, 'All'),
        React.createElement('option', { value: 'personal' }, 'Personal'),
        React.createElement('option', { value: 'work' }, 'Work')
      ),

      React.createElement('label', null, 'Filter Status: '),
      React.createElement(
        'select',
        {
          value: statusFilter,
          onChange: e => setStatusFilter(e.target.value),
        },
        React.createElement('option', { value: '' }, 'All'),
        React.createElement('option', { value: 'true' }, 'Completed'),
        React.createElement('option', { value: 'false' }, 'Pending')
      )
    ),

    // Add new task input and category selector
    React.createElement(
      'div',
      { style: { marginBottom: 20 } },
      React.createElement('input', {
        type: 'text',
        placeholder: 'Task title',
        value: title,
        onChange: e => setTitle(e.target.value),
        style: { marginRight: 10 },
      }),
      React.createElement(
        'select',
        {
          value: category,
          onChange: e => setCategory(e.target.value),
          style: { marginRight: 10 },
        },
        React.createElement('option', { value: 'personal' }, 'Personal'),
        React.createElement('option', { value: 'work' }, 'Work')
      ),
      React.createElement(
        'button',
        { onClick: handleAddTask },
        'Add Task'
      )
    ),

    // List of tasks
    ...tasks.map(task =>
      React.createElement(
        'div',
        {
          key: task.id,
          style: {
            marginBottom: 10,
            padding: 10,
            backgroundColor: task.status ? '#d4edda' : '#f8d7da',
            borderRadius: 6,
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
          },
        },
        React.createElement(
          'div',
          {
            style: {
              flex: 1,
              textDecoration: task.status ? 'line-through' : 'none',
              marginRight: 10,
            },
          },
          task.title,
          ' ',
          React.createElement('small', null, `[${task.category}]`)
        ),

        React.createElement(
          'button',
          {
            onClick: () => toggleComplete(task),
            style: { marginRight: 10 },
          },
          task.status ? 'Undo' : 'Complete'
        ),

        React.createElement(
          'button',
          {
            onClick: () => deleteTask(task.id),
            style: {
              backgroundColor: '#dc3545',
              color: 'white',
              border: 'none',
              padding: '5px 10px',
              cursor: 'pointer',
            },
          },
          'Delete'
        )
      )
    )
  );
}

ReactDOM.render(React.createElement(App), document.getElementById('root'));
