            <div
              style={{
                flex: 1,
                textDecoration: task.status ? "line-through" : "none",
                marginRight: 10,
              }}
            >
              <strong>{task.title}</strong> [{task.category}]<br />
              <small>{task.description}</small>
            </div>

            <button
              onClick={() => toggleComplete(task)}
              style={{ marginRight: 10, padding: "5px 10px", cursor: "pointer" }}
