import React from "react";

export default function TaskFilters({ filters, setFilters }) {
  return React.createElement(
    "div",
    { style: { marginBottom: 20 } },
    React.createElement("input", {
      type: "text",
      placeholder: "Filter by category",
      value: filters.category,
      onChange: e => setFilters(prev => ({ ...prev, category: e.target.value })),
    }),
    " ",
    React.createElement("select", {
      value: filters.status === null ? "" : filters.status ? "true" : "false",
      onChange: e =>
        setFilters(prev => ({
          ...prev,
          status: e.target.value === "" ? null : e.target.value === "true",
        })),
    },
      React.createElement("option", { value: "" }, "Any Status"),
      React.createElement("option", { value: "true" }, "Completed"),
      React.createElement("option", { value: "false" }, "Pending")
    ),
    " ",
    React.createElement("select", {
      value: filters.priority,
      onChange: e => setFilters(prev => ({ ...prev, priority: e.target.value })),
    },
      React.createElement("option", { value: "" }, "Any Priority"),
      React.createElement("option", { value: "Low" }, "Low"),
      React.createElement("option", { value: "Medium" }, "Medium"),
      React.createElement("option", { value: "High" }, "High")
    )
  );
}
