import React, { useState, useEffect } from "react";

function App() {
  const BACKEND_URL = "http://127.0.0.1:8000";

  // State
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("personal");
  const [priority, setPriority] = useState("Low");
  const [statusFilter, setStatusFilter] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("");
  const [priorityFilter, setPriorityFilter] = useState("");
  const [summary, setSummary] = useState({
    total_tasks: 0,
    completed_tasks: 0,
    pending_tasks: 0,
    completion_rate_percent: 0,
  });

  // Edit state
  const [editingTaskId, setEditingTaskId] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editDescription, setEditDescription] = useState("");
  const [editCategory, setEditCategory] = useState("personal");
  const [editPriority, setEditPriority] = useState("Low");
  const [editStatus, setEditStatus] = useState(false);

  // Fetch tasks with filters
  function fetchTasks() {
    if (priorityFilter) {
      fetch(`${BACKEND_URL}/tasks-by-priority/?priority=${priorityFilter}`)
        .then((res) => {
          if (!res.ok) {
            if (res.status === 404) return [];
            throw new Error("No tasks found for this priority");
          }
          return res.json();
        })
        .then((data) => setTasks(data))
        .catch(() => setTasks([]));
    } else {
      const url = new URL(`${BACKEND_URL}/filter-tasks/`);
      if (categoryFilter) url.searchParams.append("category", categoryFilter);
      if (statusFilter !== "") url.searchParams.append("status", statusFilter === "true");
      fetch(url.toString())
        .then((res) => (res.ok ? res.json() : []))
        .then((data) => setTasks(data))
        .catch(() => setTasks([]));
    }
  }

  // Fetch summary
  function fetchSummary() {
    fetch(BACKEND_URL + "/tasks-summary/")
      .then((res) => res.json())
      .then((data) => setSummary(data))
      .catch(() =>
        setSummary({
          total_tasks: 0,
          completed_tasks: 0,
          pending_tasks: 0,
          completion_rate_percent: 0,
        })
      );
  }

  useEffect(() => {
    fetchTasks();
    fetchSummary();
  }, [priorityFilter, categoryFilter, statusFilter]);

  // Add task
  function handleAddTask() {
    if (title.trim() === "") return;

    const newTask = {
      title,
      description,
      category,
      priority,
      status: false,
    };

    fetch(BACKEND_URL + "/tasks/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify([newTask]), // backend expects list
    })
      .then((res) => {
        if (!res.ok) throw new Error("Failed to create task");
        return res.json();
      })
      .then(() => {
        setTitle("");
        setDescription("");
        setCategory("personal");
        setPriority("Low");
        fetchTasks();
        fetchSummary();
      })
      .catch((err) => alert("Error adding task: " + err.message));
  }

  // Toggle complete
  function toggleComplete(task) {
    fetch(`${BACKEND_URL}/tasks/${task.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ ...task, status: !task.status }),
    })
      .then(() => {
        fetchTasks();
        fetchSummary();
      })
      .catch(() => alert("Failed to update task"));
  }

  // Delete task
  function deleteTask(taskId) {
    fetch(`${BACKEND_URL}/tasks/${taskId}`, { method: "DELETE" })
      .then(() => {
        fetchTasks();
        fetchSummary();
      })
      .catch(() => alert("Failed to delete task"));
  }

  // Start editing task
  function startEdit(task) {
    setEditingTaskId(task.id);
    setEditTitle(task.title);
    setEditDescription(task.description || "");
    setEditCategory(task.category);
    setEditPriority(task.priority);
    setEditStatus(task.status);
  }

  // Cancel editing
  function cancelEdit() {
    setEditingTaskId(null);
  }

  // Submit updated task
  function submitUpdate(taskId) {
    const updatedTask = {
      title: editTitle,
      description: editDescription,
      category: editCategory,
      priority: editPriority,
      status: editStatus,
    };

    fetch(`${BACKEND_URL}/tasks/${taskId}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(updatedTask),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Failed to update task");
        return res.json();
      })
      .then(() => {
        setEditingTaskId(null);
        fetchTasks();
        fetchSummary();
      })
      .catch((err) => alert(err.message));
  }

