      React.createElement(
        "div",
        {
          key: task.id,
          style: {
            marginBottom: 10,
            padding: 10,
            backgroundColor: task.status ? "#d4edda" : "#f8d7da",
            borderRadius: 6,
          },
        },

        editingTaskId === task.id
          ? React.createElement(
              "div",
              null,
              React.createElement("input", {
                type: "text",
                value: editTitle,
                onChange: (e) => setEditTitle(e.target.value),
                placeholder: "Title",
                style: { marginRight: 10 },
              }),
              React.createElement("input", {
                type: "text",
                value: editDescription,
                onChange: (e) => setEditDescription(e.target.value),
                placeholder: "Description",
                style: { marginRight: 10 },
              }),
              React.createElement(
                "select",
                {
                  value: editCategory,
                  onChange: (e) => setEditCategory(e.target.value),
                  style: { marginRight: 10 },
                },
                React.createElement("option", { value: "personal" }, "Personal"),
                React.createElement("option", { value: "work" }, "Work")
              ),
              React.createElement(
                "select",
                {
                  value: editPriority,
                  onChange: (e) => setEditPriority(e.target.value),
                  style: { marginRight: 10 },
                },
                React.createElement("option", { value: "Low" }, "Low"),
                React.createElement("option", { value: "Medium" }, "Medium"),
                React.createElement("option", { value: "High" }, "High")
              ),
              React.createElement(
                "select",
                {
                  value: editStatus.toString(),
                  onChange: (e) => setEditStatus(e.target.value === "true"),
                  style: { marginRight: 10 },
                },
                React.createElement("option", { value: "false" }, "Pending"),
                React.createElement("option", { value: "true" }, "Completed")
              ),
              React.createElement(
                "button",
                { onClick: () => submitUpdate(task.id), style: { marginRight: 10 } },
                "Save"
              ),
              React.createElement(
                "button",
                { onClick: cancelEdit },
                "Cancel"
              )
            )
          : React.createElement(
              "div",
              {
                style: {
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                },
              },
              React.createElement(
                "div",
                {
                  style: {
                    flex: 1,
                    textDecoration: task.status ? "line-through" : "none",
                    marginRight: 10,
                  },
                },
                `${task.title} [${task.category}] (Priority: ${task.priority})`,
                React.createElement("br"),
                React.createElement("small", null, task.description || "")
              ),
              React.createElement(
                "button",
                {
                  onClick: () => startEdit(task),
                  style: { marginRight: 10 },
                },
                "Edit"
              ),
              React.createElement(
                "button",
                {
                  onClick: () => toggleComplete(task),
                  style: { marginRight: 10 },
                },
                task.status ? "Undo" : "Complete"
              ),
              React.createElement(
                "button",
                {
                  onClick: () => deleteTask(task.id),
                  style: {
                    backgroundColor: "#dc3545",
                    color: "white",
                    border: "none",
                    padding: "5px 10px",
                    cursor: "pointer",
                  },
                },
                "Delete"
              )
            )
      )
    )
  );
}

export default App;
