import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import "../styles/TaskSummary.css";

const TaskSummary = () => {
  const { token } = useContext(AuthContext);
  const [summary, setSummary] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get("http://localhost:8000/tasks-summary", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setSummary(res.data))
      .catch(() => setError("Failed to load summary"));
  }, [token]);

  if (error) return <p className="error-message">{error}</p>;
  if (!summary) return <p>Loading summary...</p>;

  return (
    <div className="summary-container">
      <h2>Task Summary</h2>
      <ul className="summary-list">
        <li>Total Tasks: <span>{summary.total_tasks}</span></li>
        <li>Completed Tasks: <span>{summary.completed_tasks}</span></li>
        <li>Pending Tasks: <span>{summary.pending_tasks}</span></li>
        <li>Completed Percentage: <span>{summary.completed_percentage}%</span></li>
      </ul>
    </div>
  );
};

export default TaskSummary;
