import React, { useEffect, useState } from "react";
import { useAuth } from "../utils/AuthContext";

export default function Tasks() {
  const { token, logout } = useAuth();
  const [tasks, setTasks] = useState([]);
  const [summary, setSummary] = useState(null);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    category: "",
    priority: "Low",
    status: false,
  });

  const fetchTasks = () => {
    fetch("http://localhost:8000/tasks/", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then(setTasks);
  };

  const fetchSummary = () => {
    fetch("http://localhost:8000/tasks-summary", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then(setSummary);
  };

  useEffect(() => {
    fetchTasks();
    fetchSummary();
  }, [token]);

  const handleCreate = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:8000/tasks/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(formData),
    });
    if (res.ok) {
      setFormData({ title: "", description: "", category: "", priority: "Low", status: false });
      fetchTasks();
      fetchSummary();
    }
  };

  const handleDelete = async (id) => {
    await fetch(`http://localhost:8000/tasks/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });
    fetchTasks();
    fetchSummary();
  };

  const handleToggleStatus = async (task) => {
    await fetch(`http://localhost:8000/tasks/${task.id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ ...task, status: !task.status }),
    });
    fetchTasks();
    fetchSummary();
  };

  return (
    <div className="p-4">
      <div className="flex justify-between items-center">
        <h2 className="text-xl font-bold">Your Tasks</h2>
        <button onClick={logout} className="bg-red-500 text-white px-4 py-2">Logout</button>
      </div>

      {summary && (
        <div className="my-4">
          <p><strong>Summary:</strong></p>
          <p>Total: {summary.total_tasks}, Completed: {summary.completed_tasks}, Completion Rate: {summary.percent_completed}%</p>
        </div>
      )}

      <form onSubmit={handleCreate} className="my-4 space-y-2">
        <input value={formData.title} onChange={(e) => setFormData({ ...formData, title: e.target.value })} placeholder="Title" className="block border p-2 w-full" required />
        <input value={formData.description} onChange={(e) => setFormData({ ...formData, description: e.target.value })} placeholder="Description" className="block border p-2 w-full" />
        <input value={formData.category} onChange={(e) => setFormData({ ...formData, category: e.target.value })} placeholder="Category" className="block border p-2 w-full" />
        <select value={formData.priority} onChange={(e) => setFormData({ ...formData, priority: e.target.value })} className="block border p-2 w-full">
          <option value="Low">Low</option>
          <option value="Medium">Medium</option>
          <option value="High">High</option>
        </select>
        <button type="submit" className="bg-green-500 text-white px-4 py-2">Add Task</button>
      </form>

      <h3 className="font-semibold mt-4">All Tasks</h3>
      <ul className="mb-4">
        {tasks.map((task) => (
          <li key={task.id} className="border-b py-2">
            <strong>{task.title}</strong> - {task.category} - {task.priority} - {task.status ? "Done" : "Pending"}
            <div className="flex space-x-2 mt-1">
              <button onClick={() => handleToggleStatus(task)} className="text-sm text-blue-600">Toggle</button>
              <button onClick={() => handleDelete(task.id)} className="text-sm text-red-600">Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
