// src/components/NavBar.js
/*import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../utils/AuthContext";

export default function NavBar() {
  const { token, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <nav className="bg-gray-800 text-white p-4 flex justify-between items-center">
      <div className="text-lg font-bold">
        <Link to="/">Smart Task Tracker</Link>
      </div>
      <div className="space-x-4">
        {!token ? (
          <>
            <Link to="/login" className="hover:underline">Login</Link>
            <Link to="/signup" className="hover:underline">Signup</Link>
          </>
        ) : (
          <>
            <Link to="/tasks" className="hover:underline">My Tasks</Link>
            <button onClick={handleLogout} className="hover:underline">Logout</button>
          </>
        )}
      </div>
    </nav>
  );
}
*/
// src/App.js
import React from "react";

import { Outlet } from "react-router-dom";

export default function App() {
  return (
    <>
      <NavBar />
      <div className="p-4">
        <h1 className="text-2xl font-bold">Welcome to Smart Task Tracker</h1>
        <p className="text-gray-600">
          Please <a href="/login" className="text-blue-500 underline">Login</a> or{" "}
          <a href="/signup" className="text-blue-500 underline">Signup</a> to get started.
        </p>
      </div>
    </>
  );
}
