import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';

function App() {
  const BACKEND_URL = 'http://127.0.0.1:8000';

  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [category, setCategory] = useState('personal');
  const [priority, setPriority] = useState('Low');
  const [filterCategory, setFilterCategory] = useState('');
  const [filterStatus, setFilterStatus] = useState('');
  const [filterPriority, setFilterPriority] = useState('');
  const [summary, setSummary] = useState({
    total_tasks: 0,
    completed_tasks: 0,
    pending_tasks: 0,
    completion_rate_percent: 0,
  });

  // Fetch tasks with optional filters
  function fetchTasks() {
    let url = new URL(BACKEND_URL + '/filter-tasks/');
    if (filterCategory) url.searchParams.append('category', filterCategory);
    if (filterStatus !== '') url.searchParams.append('status', filterStatus === 'true');
    if (filterPriority) url.searchParams.append('priority', filterPriority);

    fetch(url.toString())
      .then(res => {
        if (!res.ok) {
          if (res.status === 404) return [];
          throw new Error('Error fetching tasks');
        }
        return res.json();
      })
      .then(data => setTasks(data))
      .catch(() => setTasks([]));
  }

  // Fetch summary data
  function fetchSummary() {
    fetch(BACKEND_URL + '/tasks-summary/')
      .then(res => res.json())
      .then(data => setSummary(data))
      .catch(() => setSummary({
        total_tasks: 0,
        completed_tasks: 0,
        pending_tasks: 0,
        completion_rate_percent: 0,
      }));
  }

  // Initial load and reload on filter change
  useEffect(() => {
    fetchTasks();
    fetchSummary();
  }, [filterCategory, filterStatus, filterPriority]);

  // Add task
  function handleAddTask() {
    if (title.trim() === '') return;

    const newTask = {
      title,
      description: '',
      category,
      priority,
      status: false,
    };

    fetch(BACKEND_URL + '/tasks/', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify([newTask]) // Backend expects list
    })
      .then(res => res.json())
      .then(createdTasks => {
        setTitle('');
        fetchTasks();
        fetchSummary();
      });
  }

  // Toggle complete
  function toggleComplete(task) {
    fetch(`${BACKEND_URL}/tasks/${task.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...task, status: !task.status })
    })
      .then(res => res.json())
      .then(updatedTask => {
        fetchTasks();
        fetchSummary();
      });
  }

  // Delete task
  function deleteTask(taskId) {
    fetch(`${BACKEND_URL}/tasks/${taskId}`, {
      method: 'DELETE'
    })
      .then(() => {
        fetchTasks();
        fetchSummary();
      });
  }

  return React.createElement(
    'div',
    { style: { padding: 20, fontFamily: 'Arial, sans-serif', maxWidth: 600, margin: 'auto' } },

    React.createElement('h2', null, 'Smart Task Tracker'),

    // Summary
    React.createElement('div', { style: { marginBottom: 20, background: '#f9f9f9', padding: 10, borderRadius: 6 } },
      React.createElement('div', null, `Total Tasks: ${summary.total_tasks}`),
      React.createElement('div', null, `Completed: ${summary.completed_tasks}`),
      React.createElement('div', null, `Pending: ${summary.pending_tasks}`),
      React.createElement('div', null, `Completion Rate: ${summary.completion_rate_percent}%`)
    ),

    // Filters
    React.createElement('div', { style: { marginBottom: 20 } },
      React.createElement('label', null, 'Filter Category: '),
      React.createElement('select', {
        value: filterCategory,
        onChange: e => setFilterCategory(e.target.value),
        style: { marginRight: 15 }
      },
        React.createElement('option', { value: '' }, 'All'),
        React.createElement('option', { value: 'personal' }, 'Personal'),
        React.createElement('option', { value: 'work' }, 'Work')
      ),

      React.createElement('label', null, 'Filter Status: '),
      React.createElement('select', {
        value: filterStatus,
        onChange: e => setFilterStatus(e.target.value),
        style: { marginRight: 15 }
      },
        React.createElement('option', { value: '' }, 'All'),
        React.createElement('option', { value: 'true' }, 'Completed'),
        React.createElement('option', { value: 'false' }, 'Pending')
      ),

      React.createElement('label', null, 'Filter Priority: '),
      React.createElement('select', {
        value: filterPriority,
        onChange: e => setFilterPriority(e.target.value),
      },
        React.createElement('option', { value: '' }, 'All'),
        React.createElement('option', { value: 'Low' }, 'Low'),
        React.createElement('option', { value: 'Medium' }, 'Medium'),
        React.createElement('option', { value: 'High' }, 'High')
      )
    ),

    // Add Task form
    React.createElement('div', { style: { marginBottom: 20 } },
      React.createElement('input', {
        type: 'text',
        placeholder: 'Task title',
        value: title,
        onChange: e => setTitle(e.target.value),
        style: { marginRight: 10 }
      }),

      React.createElement('select', {
        value: category,
        onChange: e => setCategory(e.target.value),
        style: { marginRight: 10 }
      },
        React.createElement('option', { value: 'personal' }, 'Personal'),
        React.createElement('option', { value: 'work' }, 'Work')
      ),

      React.createElement('select', {
        value: priority,
        onChange: e => setPriority(e.target.value),
        style: { marginRight: 10 }
      },
        React.createElement('option', { value: 'Low' }, 'Low'),
        React.createElement('option', { value: 'Medium' }, 'Medium'),
        React.createElement('option', { value: 'High' }, 'High')
      ),

      React.createElement('button', { onClick: handleAddTask }, 'Add Task')
    ),

    // Task list
    ...tasks.map(task =>
      React.createElement(
        'div',
        {
          key: task.id,
          style: {
            marginBottom: 10,
            padding: 10,
            backgroundColor: task.status ? '#d4edda' : '#f8d7da',
            borderRadius: 5,
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
          }
        },

        React.createElement('div', {
          style: {
            textDecoration: task.status ? 'line-through' : 'none',
            flex: 1,
            marginRight: 10,
          }
        },
          React.createElement('strong', null, task.title),
          ' ',
          React.createElement('small', null, `[${task.category} | ${task.priority}]`)
        ),

        React.createElement('button', {
          onClick: () => toggleComplete(task),
          style: { marginRight: 10 }
        }, task.status ? 'Undo' : 'Complete'),

        React.createElement('button', {
          onClick: () => deleteTask(task.id),
          style: { backgroundColor: '#dc3545', color: 'white', border: 'none', padding: '5px 10px', cursor: 'pointer' }
        }, 'Delete')
      )
    )
  );
}

ReactDOM.render(
  React.createElement(App),
  document.getElementById('root')
);
