import React, { useState, useEffect, useCallback } from "react";
import { useAuth } from "../utils/AuthContext";

export default function Tasks() {
  const BACKEND_URL = "http://localhost:8000";
  const { token, logout } = useAuth();

  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("personal");
  const [status, setStatus] = useState(false);
  const [error, setError] = useState(null);

  const fetchData = useCallback(() => {
    fetch(BACKEND_URL + "/tasks/", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => {
        if (res.status === 401) {
          logout();
          throw new Error("Unauthorized");
        }
        if (!res.ok) throw new Error("Failed to fetch tasks");
        return res.json();
      })
      .then((data) => setTasks(data))
      .catch((err) => setError(err.message));
  }, [token, logout]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  const handleAddTask = () => {
    if (!title.trim()) {
      alert("Title is required");
      return;
    }
    const newTask = {
      title,
      description,
      category,
      status,
    };
    fetch(BACKEND_URL + "/tasks/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify([newTask]), // backend expects an array
    })
      .then((res) => {
        if (res.status === 401) {
          logout();
          throw new Error("Unauthorized");
        }
        if (!res.ok) throw new Error("Failed to add task");
        return res.json();
      })
      .then(() => {
        setTitle("");
        setDescription("");
        setCategory("personal");
        setStatus(false);
        fetchData();
      })
      .catch((err) => alert(err.message));
  };

  const toggleComplete = (task) => {
    fetch(`${BACKEND_URL}/tasks/${task.id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ ...task, status: !task.status }),
    })
      .then((res) => {
        if (res.status === 401) {
          logout();
          throw new Error("Unauthorized");
        }
        if (!res.ok) throw new Error("Failed to update task");
        return res.json();
      })
      .then(fetchData)
      .catch((err) => alert(err.message));
  };

  const deleteTask = (taskId) => {
    fetch(`${BACKEND_URL}/tasks/${taskId}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => {
        if (res.status === 401) {
          logout();
          throw new Error("Unauthorized");
        }
        if (!res.ok) throw new Error("Failed to delete task");
        return res.json();
      })
      .then(fetchData)
      .catch((err) => alert(err.message));
  };

  return (
    <div className="max-w-3xl mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Your Tasks</h2>

      {/* Add Task Form */}
      <div className="mb-6 p-4 border rounded shadow-sm bg-gray-50">
        <h3 className="text-xl mb-2 font-semibold">Add New Task</h3>
        <input
          className="border p-2 mb-2 w-full"
          type="text"
          placeholder="Title *"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <input
          className="border p-2 mb-2 w-full"
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <select
          className="border p-2 mb-2 w-full"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="personal">Personal</option>
          <option value="work">Work</option>
        </select>
        <label className="inline-flex items-center mb-4">
          <input
            type="checkbox"
            checked={status}
            onChange={(e) => setStatus(e.target.checked)}
            className="mr-2"
          />
          Completed
        </label>
        <button
          onClick={handleAddTask}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Add Task
        </button>
      </div>

      {/* Error message */}
      {error && <p className="text-red-600 mb-4">{error}</p>}

      {/* Task List */}
      {tasks.length === 0 ? (
        <p>No tasks found.</p>
      ) : (
        tasks.map((task) => (
          <div
            key={task.id}
            className={`flex justify-between items-center mb-3 p-3 rounded ${
              task.status ? "bg-green-100" : "bg-red-100"
            }`}
          >
            <div>
              <h4
                className={`font-semibold ${
                  task.status ? "line-through" : ""
                }`}
              >
                {task.title} [{task.category}]
              </h4>
              <p>{task.description}</p>
            </div>
            <div>
              <button
                onClick={() => toggleComplete(task)}
                className="mr-2 px-3 py-1 border rounded hover:bg-gray-200"
              >
                {task.status ? "Undo" : "Complete"}
              </button>
              <button
                onClick={() => deleteTask(task.id)}
                className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700"
              >
                Delete
              </button>
            </div>
          </div>
        ))
      )}
    </div>
  );
}

