import React, { useEffect, useState } from "react";
import { useAuth } from "../utils/AuthContext";
//import { useAuth } from "../utils/AuthContext";


export default function Tasks() {
  const { token, logout } = useAuth();

  const [tasks, setTasks] = useState([]);
  const [summary, setSummary] = useState({ total_tasks: 0, completed_tasks: 0, percent_completed: 0 });
  const [loading, setLoading] = useState(true);
  const [newTaskTitle, setNewTaskTitle] = useState("");
  const [newTaskCategory, setNewTaskCategory] = useState("Low");

  const API_BASE = "http://localhost:8000"; // your backend URL

  // Fetch tasks & summary
  const fetchData = async () => {
    setLoading(true);
    try {
      const resTasks = await fetch(`${API_BASE}/tasks/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!resTasks.ok) throw new Error("Failed to fetch tasks");
      const dataTasks = await resTasks.json();

      const resSummary = await fetch(`${API_BASE}/tasks-summary`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!resSummary.ok) throw new Error("Failed to fetch summary");
      const dataSummary = await resSummary.json();

      setTasks(dataTasks);
      setSummary(dataSummary);
    } catch (error) {
      alert(error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // Toggle task status (complete/incomplete)
  const toggleStatus = async (task) => {
    try {
      const updatedTask = {
        ...task,
        status: !task.status,
      };
      const res = await fetch(`${API_BASE}/tasks/${task.id}`, {
        method: "PUT",
        headers: {
          "Authorization": `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(updatedTask),
      });
      if (!res.ok) throw new Error("Failed to update task");
      await fetchData();
    } catch (error) {
      alert(error.message);
    }
  };

  // Delete task
  const deleteTask = async (taskId) => {
    if (!window.confirm("Are you sure you want to delete this task?")) return;
    try {
      const res = await fetch(`${API_BASE}/tasks/${taskId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!res.ok) throw new Error("Failed to delete task");
      await fetchData();
    } catch (error) {
      alert(error.message);
    }
  };

  // Add new task
  const addTask = async () => {
    if (!newTaskTitle.trim()) {
      alert("Please enter a task title");
      return;
    }
    try {
      const newTask = {
        title: newTaskTitle,
        description: "",
        category: newTaskCategory,
        status: false,
        priority: newTaskCategory,
      };
      const res = await fetch(`${API_BASE}/tasks/`, {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newTask),
      });
      if (!res.ok) throw new Error("Failed to add task");
      setNewTaskTitle("");
      setNewTaskCategory("Low");
      await fetchData();
    } catch (error) {
      alert(error.message);
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div style={{ padding: 20, maxWidth: 700, margin: "auto" }}>
      <header style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h1>Smart Task Tracker</h1>
        <button onClick={logout} style={{ padding: "6px 12px" }}>Logout</button>
      </header>

      <section style={{ marginTop: 20 }}>
        <h2>Summary</h2>
        <p>Total Tasks: {summary.total_tasks}</p>
        <p>Completed: {summary.completed_tasks}</p>
        <p>Completion Rate: {summary.percent_completed.toFixed(2)}%</p>
      </section>

      <section style={{ marginTop: 30 }}>
        <h2>Add Task</h2>
        <input
          type="text"
          placeholder="Task title"
          value={newTaskTitle}
          onChange={(e) => setNewTaskTitle(e.target.value)}
          style={{ padding: 6, width: "60%", marginRight: 10 }}
        />
        <select
          value={newTaskCategory}
          onChange={(e) => setNewTaskCategory(e.target.value)}
          style={{ padding: 6, marginRight: 10 }}
        >
          <option>Low</option>
          <option>Medium</option>
          <option>High</option>
        </select>
        <button onClick={addTask} style={{ padding: "6px 12px" }}>Add Task</button>
      </section>

      <section style={{ marginTop: 40 }}>
        <h2>All Tasks</h2>
        {tasks.length === 0 && <p>No tasks found.</p>}
        {tasks.map((task) => (
          <div
            key={task.id}
            style={{
              border: "1px solid #ddd",
              padding: 12,
              marginBottom: 10,
              borderRadius: 4,
              backgroundColor: task.status ? "#d4edda" : "#f8d7da",
            }}
          >
            <h3>{task.title}</h3>
            <p><strong>Category:</strong> {task.category}</p>
            <p><strong>Status:</strong> {task.status ? "Done" : "Pending"}</p>

            <button
              onClick={() => toggleStatus(task)}
              style={{ marginRight: 10, padding: "4px 10px" }}
            >
              {task.status ? "Mark Incomplete" : "Mark Complete"}
            </button>

            <button
              onClick={() => deleteTask(task.id)}
              style={{ padding: "4px 10px", backgroundColor: "#dc3545", color: "#fff", border: "none" }}
            >
              Delete
            </button>
          </div>
        ))}
      </section>
    </div>
  );
}

