import React, { useEffect, useState } from "react";
import * as api from "./api/tasks";
import TaskForm from "./components/TaskForm";
import TaskList from "./components/TaskList";
import TaskFilters from "./components/TaskFilters";
import TaskSummary from "./components/TaskSummary";

export default function App() {
  const [tasks, setTasks] = useState([]);
  const [filters, setFilters] = useState({ category: "", status: null, priority: "" });
  const [summary, setSummary] = useState({});

  useEffect(() => {
    async function loadTasks() {
      try {
        let data;
        if (filters.priority) {
          data = await api.fetchTasksByPriority(filters.priority);
        } else if (filters.category || filters.status !== null) {
          data = await api.fetchFilteredTasks(filters);
        } else {
          data = await api.fetchTasks();
        }
        setTasks(data);
      } catch (err) {
        alert("Error loading tasks: " + err.message);
        setTasks([]);
      }
    }
    loadTasks();
  }, [filters]);

  useEffect(() => {
    api.fetchTaskSummary().then(setSummary);
  }, [tasks]);

  const addTasks = async (newTasks) => {
    const created = await api.createTasks(newTasks);
    setTasks((prev) => [...prev, ...created]);
  };

  const updateTask = async (id, updates) => {
    const updated = await api.updateTask(id, updates);
    setTasks((prev) => prev.map((t) => (t.id === id ? updated : t)));
  };

  const deleteTask = async (id) => {
    await api.deleteTask(id);
    setTasks((prev) => prev.filter((t) => t.id !== id));
  };

  return React.createElement(
    "div",
    { style: { maxWidth: 700, margin: "auto", padding: 20 } },
    React.createElement("h1", null, "Smart Task Tracker"),
    React.createElement(TaskSummary, { summary }),
    React.createElement(TaskFilters, { filters, setFilters }),
    React.createElement(TaskForm, { onAdd: addTasks }),
    React.createElement(TaskList, { tasks, onUpdate: updateTask, onDelete: deleteTask })
  );
}
