import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import "./TaskSummary.css"; // Assuming you have some styles for the summary

const TaskSummary = () => {
  const { token } = useContext(AuthContext);
  const [summary, setSummary] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get("http://localhost:8000/tasks-summary", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setSummary(res.data))
      .catch(() => setError("Failed to load summary"));
  }, [token]);

  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!summary) return <p>Loading summary...</p>;

  return (
    <div>
      <h2>Task Summary</h2>
      <ul>
        <li>Total Tasks: {summary.total_tasks}</li>
        <li>Completed Tasks: {summary.completed_tasks}</li>
        <li>Pending Tasks: {summary.pending_tasks}</li>
        <li>Completed Percentage: {summary.completed_percentage}%</li>
      </ul>
    </div>
  );
};

export default TaskSummary;
